class Node:
    index = -1
    via = -1
    dist = 6000
    next = None
    def __str__(self) -> str:
        return "{}{}{}{}{}{}".format("index=", self.index, ", via=", self.via, ", dist=", self.dist)
    def __repr__(self):
        return "{"+str(self)+"}"
    
    def __init__(self, index, via, dist):
        self.index = index
        self.via = via
        self.dist = dist
        self.next = None

