class LinkedList:
    head = None
    tail = None
    length = 0
    def __init__(self):
        self.head = None
        self.tail = None
        self.length = 0
    def __repr__(self):
        node = self.head
        nodes = []
        while node is not None:
            nodes.append(str(node))
            node = node.next
        nodes.append("None")
        return " -> ".join(nodes)
    def append(self, n: Node):
        if(self.head == None):
            self.head = n
            self.tail = n
            self.length = 1
        else:
            self.tail.next = n
            self.tail = n
            self.length += 1
    def pop(self):
        if(self.head == None):
            return None
        else:
            n = self.head
            self.head = self.head.next
            self.length -= 1
            return n
    def contains(self, index) -> bool:
        if(self.head == None):
            return False
        else:
            curr = self.head
            while(curr != None): 
                if(curr.index == index): return True
                curr = curr.next
            return False

